arguments.most_failing:
{% for data in vars.input.params['fortisoar_health_data']['fortisoar_information']['top_10_most_failing_playbooks']  %}
            <tr class="flex_one border_none flex_display padding_zero table_row_color margin_zero">
                <td class="width_16per padding_1rem_top_and_bottom font_weight_lighter table_top_border padding_6per_left text_overflow_hidden">{{loop.index}}</td>
                <td class="width_57per padding_1rem_top_and_bottom font_weight_lighter table_top_border text_overflow_hidden"><a href='https://{{globalVars.Server_fqhn}}/playbooks/{{data.playbook_iri | regex_replace("/api/3/workflows/", "")}}' target='_blank'>{{data.playbook_name}} </a></td>
                <td class="table_top_border font_weight_lighter padding_1rem_top_and_bottom text_overflow_hidden width_26per">{{data.execution_count}}</td>
            </tr>{% endfor%}

