---

optimal:
  elasticsearch:
    mask: False
    # Below values are in GB
    xms: 4
    xmx: 4
  postgres:
    max_worker_processes: {{vars.input.params['configuration_recommandation']['postgresql']['max_worker_processes'] | int }}
    max_parallel_workers_per_gather: {{vars.input.params['configuration_recommandation']['postgresql']['max_parallel_workers_per_gather'] | int }}
    max_parallel_maintenance_workers: {{vars.input.params['configuration_recommandation']['postgresql']['max_parallel_maintenance_workers'] | int }}
    max_parallel_workers: {{vars.input.params['configuration_recommandation']['postgresql']['max_parallel_workers'] | int }}
    max_connections: {{vars.input.params['configuration_recommandation']['postgresql']['max_connections'] | int}}
    checkpoint_completion_target: {{vars.input.params['configuration_recommandation']['postgresql']['checkpoint_completion_target']}}
    default_statistics_target: 100
    random_page_cost: {{vars.input.params['configuration_recommandation']['postgresql']['random_page_cost']  }}
    effective_io_concurrency: {{vars.input.params['configuration_recommandation']['postgresql']['effective_io_concurrency'] }}
    huge_pages: try
    # Below values are in GB
    min_wal_size: {{vars.input.params['configuration_recommandation']['postgresql']['min_wal_size'] | regex_replace("GB","") | int}}
    max_wal_size: {{vars.input.params['configuration_recommandation']['postgresql']['max_wal_size'] | regex_replace("GB","") | int}}
    effective_cache_size: {{vars.input.params['configuration_recommandation']['postgresql']['effective_cache_size'] | regex_replace("GB","") | int}}
    # Below values are in MB
    shared_buffer: {{vars.input.params['configuration_recommandation']['postgresql']['shared_buffers'] | regex_replace("MB","") | int}}
    work_mem: {{vars.input.params['configuration_recommandation']['postgresql']['work_mem'] | regex_replace("MB","") | int}}
    maintenance_work_mem: {{vars.input.params['configuration_recommandation']['postgresql']['maintenance_work_mem'] | regex_replace("MB","") | int}}
    wal_buffers: {{vars.input.params['configuration_recommandation']['postgresql']['wal_buffers'] | regex_replace("MB","") | int}}
  tomcat:
    # Below values are in MB
    xms: 256
    xmx: 4096
  php:
    max_children: 20
    start_servers: {{vars.input.params['configuration_recommandation']['php_fpm']['pm.start_servers'] | int}}
    min_spare_servers: 4
    max_spare_servers: {{vars.input.params['configuration_recommandation']['php_fpm']['pm.max_spare_servers'] | int}}
    # Below value is in MB
    acpu: 512
    admin_memory_limit: 1024
  mq:
    scheduler: {{vars.input.params['configuration_recommandation']['rabbit_mq']| regex_search("[\d]+") | int}}
  workflow:
    autoscale: True
    max_consumer: {% set celeryd_values=vars.input.params['configuration_recommandation']['celeryd']| regex_findall("[\d]+") %}{{celeryd_values[0] | int}}
    min_consumer: {{celeryd_values[1] | int}}
    process: {{vars.input.params['configuration_recommandation']['sealab_wsgi'] | int}}
    rule_engine_worker: 8
  hardware:
    # Number of cores
    cpu: {{vars.input.params['system_details']['CPU'] | int}}
    # Below value is in GB
    ram: {{vars.input.params['system_details']['RAM'] | int}}
    ram_percent_error_margin: 10.00
